import React, { Component } from 'react';

class App extends Component {
  state = {
    firstName: '',
  }

  handleSubmit = (e) => {
    e.preventDefault()
    this.setState({
      firstName: ''
    })
  }
  handleChange = (e) => {
    e.preventDefault()
    const { name, value } = e
    this.setState( {
      [name]: value
    })

    console.log("change", this.state.firstName)
  }


  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <input
            placeholder="first name"
            value={this.state.firstName}
            name="firstName"
            type="text"
            onChange={this.handleChange}
          />
          <button>submit</button>
        </form>
      </div>
    );
  }
}

export default App;
